import React, { Fragment, PureComponent } from "react";
import Description_Link from "../../shared/description_with_links";
import GrayImg from "../../shared/gray_img";

async function getSattelities(){ 
    let response_mars = await fetch("http://localhost:3000/api/mars.json")
    let data_mars = await response_mars.json()
    let response_pluto = await fetch("http://localhost:3000/api/pluto.json")
    let data_pluto = await response_pluto.json()
    let response_uranus = await fetch("http://localhost:3000/api/uranus.json")
    let data_uranus = await response_uranus.json()
    let data = `${data_mars} + ${data_pluto} + ${data_uranus}`
    return data;
}



class Planet extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            mars_moon: [
            ]
        }

    }

    componentDidMount() {
        getSattelities().then(data => {
            this.setState(state => ({
                planets: data['planet']
            }))
        })
    }

    render = () => {
        let title;

        if (props.title_with_underline) {
            title = <h4><u>{props.name}</u></h4>
        } else {
            title = <h4>{props.name}</h4>
        }
        return (
            <div>
                {title}
                <p />
                <Description_Link description={props.description} link={props.link} link_name={props.link_name} />
                <GrayImg img_url={props.img_url} gray={props.gray} />
                <hr />
            </div>
        )
    }



}

export default Planet;
