import React, { Fragment } from "react";
import Planet from "./planet";


class Planets extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            planets: [{
                name: "Mercúrio",
                description: "Para o deus da mitologia romana, consulte Mercúrio (mitologia); para demais casos, veja Mercúrio. Mercúrio é o menor e mais interno planeta do Sistema Solar, orbitando o Sol a cada 87,969 dias terrestres.",
                img_url: "./img/mercurio_color.jpg",
                link: "https://pt.wikipedia.org/wiki/Merc%C3%BArio_(planeta)",
                link_name: "Veja mais na seção de Plutão na Wikipedia clicando aqui",
                title_with_underline: true,
                gray: true
            },
            {
                name: "Plutão",
                description: "Plutão, formalmente designado 134340 Plutão (símbolos: ⯓ e ♇) é um planeta anão do Sistema Solar e o nono maior e décimo mais massivo objeto observado diretamente orbitando o Sol. Originalmente classificado como um planeta, Plutão é atualmente o maior membro conhecido do cinturão de Kuiper,[11] uma região de corpos além da órbita de Netuno.",
                img_url: "./img/plutao_color.jpg",
                link: "https://pt.wikipedia.org/wiki/Plut%C3%A3o",
                link_name: "Veja mais na seção de Plutão na Wikipedia clicando aqui",
                title_with_underline: false,
                gray: true,
            }
            ]
        }
    }
    render() {
        return (
            <Fragment>
                <h3>Planets list</h3>
                <button>Show me!</button>
                <hr />
                <Planet
                    name={this.state.planets[0].name}
                    description={this.state.planets[0].description}
                    img_url={this.state.planets[0].img_url}
                    link={this.state.planets[0].link}
                    link_name={this.state.planets[0].link_name}
                    title_with_underline={this.state.planets[0].title_with_underline}
                    gray={this.state.planets[0].gray}
                />
                <Planet
                    name={this.state.planets[1].name}
                    description={this.state.planets[1].description}
                    img_url={this.state.planets[1].img_url}
                    link={this.state.planets[1].link}
                    link_name={this.state.planets[1].link_name}
                    title_with_underline={this.state.planets[1].title_with_underline}
                    gray={this.state.planets[1].gray}
                />
            </Fragment>
        )
    }
}

export default Planets;
