import React, { Fragment, PureComponent } from "react";
import Planets from "..";
import Description_Link from "../../shared/description_with_links";
import GrayImg from "../../shared/gray_img";

async function getSattelites(id){ 
    let response = await fetch(`http://localhost:3000/api/${id}.json`)
    let data = await response.json()
    return data;
}

class Planet extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            satellites: [
            ]
        }

    }

    componentDidMount() {
        let id = this.props.id
        getSattelites(id).then(data => {
            this.setState(state => ({
                satellites: data['satellites']
            }))
        })
    }

    render = () => {
        let title;

        if (this.props.title_with_underline) {
            title = <h4><u>{this.props.name}</u></h4>
        } else {
            title = <h4>{this.props.name}</h4>
        }
        return (
            <div>
                {title}
                <p />
                <Description_Link description={this.props.description} link={this.props.link} link_name={this.props.link_name} />
                <GrayImg img_url={this.props.img_url} gray={this.props.gray} />
                <h4>Sattelites</h4>
                <hr/>   
            </div>
        )
    }
}

export default Planet;
