import React, { Fragment } from "react";
import './css/style.css';

const Login = () => {
    //document.body.style.backgroundImage = "url(../img/background.jpg)";
    document.body.style.backgroundSize = "cover";
    //document.body.style.backgroundPositionY = "-130px";
    document.body.style.backgroundRepeat = "no-repeat";
    document.body.style.backgroundAttachment = "inherit";
    document.body.style.scrollBehavior = "auto";


    return (
        <Fragment>
            <script rel="stylesheet" type="text/css" href="css/style.css">
            </script>   
            <div id="login-container">
                <h1>Login</h1>
                <form action="" method="post">
                    <label for="email">E-mail</label>
                    <input type="email" name="email" id="email" placeholder="Entre com seu e-mail" autocomplete="off" />
                    <label for="">Senha</label>
                    <input type="password" name="password" id="password" placeholder="Digite sua senha" />
                    <a href="#" id="forgot-pass">Esqueceu sua senha?</a>
                    <input type="submit" value="Login" />
                </form>
                <div id="social-container">
                    <p>Ou entre pelas suas redes sociais</p>
                    <i className="fa fa-facebook-f"></i>
                    <i className="fa fa-linkedin"></i>
                    <i className="fa fa-twitter"></i>
                </div>
                <div id="register-container">
                    <p>Ainda não tem uma conta ?</p>
                    <a href="/registro">Registrar</a>
                </div>
            </div>
        </Fragment>
    )
}

export default Login;
