import Planet from "./planet";

const Planets = () => {
    return (
        <Fragment>
        <h3>Planets list</h3>
        <hr/>
        <Planet name="Mercúrio"
        description="
        Para o deus da mitologia romana, consulte Mercúrio (mitologia); para demais casos, veja Mercúrio.
Mercúrio Planeta principal Mercury in color Fotografia de Mercúrio em cores, feita pela sonda MESSENGER em 2008.Características orbitais
