import React, { useEffect, useState } from "react";
import GrayImg from "../../shared/gray_img";
import Form from './form'

async function getSattelites(id) {
    let response = await fetch(`http://localhost:3000/api/${id}.json`)
    let data = await response.json()
    return data;
}

const Planet = (props) => {
    const [satellites, setSattelites] = useState([])

        useEffect((props) => {
            getSattelites(props.id).then(data => {
                setSattelites(data['satellites'])
            })
        }, [])

        let title;
        if (props.title_with_underline) {
            title = <h4><u>{props.name}</u></h4>
        } else {
            title = <h4>{props.name}</h4>
        }


        const addSattelites = (new_sattelite) => {
            setSattelites([...satellites, new_sattelite])
        }

    return (

        <div>
            {title}
            <p />
            <Description_Link description={props.description} link={props.link} />
            <GrayImg img_url={props.img_url} gray={props.gray} />
            <h4>Satellites</h4>
            <hr/>
            <Form addSattelites={addSattelites}>

            </Form>
            <ul>
                {satellites.map((satellite, index) =>
                    <li key={index}>{satellite.name}</li>
                )}
            </ul>

            <hr />
        </div>
    )
}

export default Planet;
